import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../App.css';
import Swal from 'sweetalert2';
import logo from '../assets/Logo.svg';
import searchIcon from '../assets/search.svg';
import wishlistIcon from '../assets/wishlist.svg';
import cartIcon from '../assets/cart.svg';
import accountIcon from '../assets/account.svg';
import LogoutIcon from "../assets/logout.svg";

const Header = () => {
  const [activeMenu, setActiveMenu] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInitial, setUserInitial] = useState('');
  const [fullName, setFullName] = useState('');
  const [showProfilePopup, setShowProfilePopup] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const updateHeaderData = () => {
      const token = localStorage.getItem('access');
      const firstName = localStorage.getItem('first_name');
      const lastName = localStorage.getItem('last_name');
  
      setIsLoggedIn(!!token);
  
      if (firstName) {
        setUserInitial(firstName.charAt(0).toUpperCase());
        setFullName(`${firstName} ${lastName || ''}`.trim());
      } else {
        setUserInitial('');
        setFullName('');
      }
    };
  
    updateHeaderData(); // Initial load
  
    // Listen for login status change from anywhere in the app
    window.addEventListener('login-status-changed', updateHeaderData);
  
    return () => {
      window.removeEventListener('login-status-changed', updateHeaderData);
    };
  }, []);
  

  const handleLogin = () => {
    navigate('/login');
  };

  const handleLogout = () => {
    localStorage.removeItem('access');
    localStorage.removeItem('refresh');
    localStorage.removeItem('first_name');
    localStorage.removeItem('last_name');
    setIsLoggedIn(false);
    setUserInitial('');
    setShowProfilePopup(false);
    navigate('/');
  };

  const handleProtectedNavigation = (path) => {
    const token = localStorage.getItem('access');
    if (!token) {
      const readablePaths = {
        '/liked-products': 'your wishlist',
        '/carted-products': 'your cart',
      };

      const friendlyName = readablePaths[path] || path;

      Swal.fire({
        icon: 'warning',
        title: 'Login Required',
        text: `Please login to view ${friendlyName}.`,
        confirmButtonColor: '#3085d6',
        confirmButtonText: 'Login'
      }).then((result) => {
        if (result.isConfirmed) {
          navigate('/login');
        }
      });
      return;
    }

    navigate(path);
  };

  const handleMenuClick = (label) => {
    setActiveMenu(label);
    setMenuOpen(false);
    document.body.style.overflow = 'auto';
  };

  const toggleHamburger = () => {
    const toggled = !menuOpen;
    setMenuOpen(toggled);
    document.body.style.overflow = toggled ? 'hidden' : 'auto';
  };

  const toggleSearch = () => {
    setShowSearch(!showSearch);
  };

  const toggleProfilePopup = () => {
    setShowProfilePopup(!showProfilePopup);
  };

  return (
    <section className="wrapper">
      <header>
        <h1>
          <a href="/">
            <img src={logo} alt="Logo" />
          </a>
        </h1>

        <ul className={`middle ${menuOpen ? 'show' : ''}`}>
          {['Shop', 'Men', 'Women', 'Combos', 'Joggers'].map((label) => (
            <li key={label} className={activeMenu === label ? 'active' : ''}>
              <a href={`/${label.toLowerCase()}`} onClick={() => handleMenuClick(label)}>
                {label}
              </a>
            </li>
          ))}
        </ul>

        <ul className="right">
          <li className="input">
            <img
              src={searchIcon}
              alt="Search"
              className="searchIcon"
              onClick={toggleSearch}
              style={{ cursor: 'pointer' }}
            />
            <input
              type="text"
              name="q"
              placeholder="Search"
              className={showSearch ? 'showinput' : ''}
            />
          </li>

          <li>
            <button onClick={() => handleProtectedNavigation('/liked-products')}>
              <img src={wishlistIcon} alt="Wishlist" />
            </button>
          </li>

          <li style={{ position: 'relative' }}>
            {!isLoggedIn ? (
              <button onClick={handleLogin}>
                <img src={accountIcon} alt="Login" />
              </button>
            ) : (
              <div
                onClick={toggleProfilePopup}
                style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  backgroundColor: 'red',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: 'bold',
                  fontSize: '30px',
                  textTransform: 'uppercase',
                  cursor: 'pointer'
                }}
                title="Click for account options"
              >
                {userInitial}
              </div>
            )}

            {showProfilePopup && (
              <div
                style={{
                  position: 'absolute',
                  top: '50px',
                  right: '-55px',
                  backgroundColor: '#fff',
                  boxShadow: '0 0 10px rgba(0,0,0,0.1)',
                  borderRadius: '8px',
                  padding: '10px',
                  zIndex: 999,
                  minWidth: '150px'
                }}
              >
                <p style={{ margin: '5px 0',textAlign:"center", fontWeight: 'bold' }}>{fullName}</p>
                <button
                  onClick={handleLogout}
                  style={{
                    padding: '6px 12px',
                    backgroundColor: '#f44336',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginTop: '8px',
                    width:"max-content",
                    margin:"0 auto"
                  }}
                >
                  Logout
                </button>
              </div>
            )}
          </li>

          <li>
            <button onClick={() => handleProtectedNavigation('/carted-products')}>
              <img src={cartIcon} alt="Cart" />
            </button>
          </li>

          
        </ul>

        <div
          className={`menubar ${menuOpen ? 'an' : ''}`}
          onClick={toggleHamburger}
        >
          <div className={`singleBar1 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar2 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar3 ${menuOpen ? 'an' : ''}`}></div>
        </div>
      </header>
    </section>
  );
};

export default Header;
