import React, { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../App.css';
import Swal from 'sweetalert2';
import logo from '../assets/Logo.svg';
import searchIcon from '../assets/search.svg';
import wishlistIcon from '../assets/wishlist.svg';
import cartIcon from '../assets/cart.svg';
import accountIcon from '../assets/account.svg';
import LogoutIcon from "../assets/logout.svg"



const Header = () => {
  const [activeMenu, setActiveMenu] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();
  const token = localStorage.getItem('access');

  useEffect(() => {
    const token = localStorage.getItem('access');
    const firstName = localStorage.getItem('first_name');
    setIsLoggedIn(!!token);
    if (firstName) {
      setUserInitial(firstName.charAt(0).toUpperCase());
    }
  }, []);
  

  const handleLogin = () => {
  navigate('/login');
};

  const handleLogout = () => {
  localStorage.removeItem('access');
  localStorage.removeItem('refresh');
  setIsLoggedIn(false);
  localStorage.removeItem('first_name');

};


const handleProtectedNavigation = (path) => {
  if (!token) {
    // Map paths to friendly names
    const readablePaths = {
      '/liked-products': 'your wishlist',
      '/carted-products': 'your cart',
    };

    const friendlyName = readablePaths[path] || path;

    Swal.fire({
      icon: 'warning',
      title: 'Login Required',
      text: `Please login to view ${friendlyName}.`,
      confirmButtonColor: '#3085d6',
      confirmButtonText: 'Login'
    }).then((result) => {
      if (result.isConfirmed) {
        navigate('/login');
      }
    });
    return;
  }

  navigate(path);
};




  const handleMenuClick = (label) => {
    setActiveMenu(label);
    setMenuOpen(false); // close mobile menu after click
    document.body.style.overflow = 'auto';
  };

  const toggleHamburger = () => {
    const toggled = !menuOpen;
    setMenuOpen(toggled);
    document.body.style.overflow = toggled ? 'hidden' : 'auto';
  };

  const toggleSearch = () => {
    setShowSearch(!showSearch);
  };

  return (
    <section className="wrapper">
      <header>
        <h1>
          <a href="/">
            <img src={logo} alt="Logo" />
          </a>
        </h1>

        <ul className={`middle ${menuOpen ? 'show' : ''}`}>
          {['Shop', 'Men', 'Women', 'Combos', 'Joggers'].map((label) => (
            <li key={label} className={activeMenu === label ? 'active' : ''}>
              <a href={`/${label.toLowerCase()}`} onClick={() => handleMenuClick(label)}>
                {label}
              </a>
            </li>
          ))}
        </ul>

        <ul className="right">
          <li className="input">
            <img
              src={searchIcon}
              alt="Search"
              className="searchIcon"
              onClick={toggleSearch}
              style={{ cursor: 'pointer' }}
            />
            <input
              type="text"
              name="q"
              placeholder="Search"
              className={showSearch ? 'showinput' : ''}
            />
          </li>

          <li>
            <button onClick={() => handleProtectedNavigation('/liked-products')}><img src={wishlistIcon} alt="Wishlist" /></button>
          </li>

          <li>
            {/* <a href="/login">
              <img src={accountIcon} alt="Account" />
            </a> */}
            {isLoggedIn ? (
  <>
    <button onClick={handleLogout}><img src={LogoutIcon} alt="Logout" /></button>
  </>
) : (
  <button onClick={handleLogin}><img src={accountIcon} alt="Login" /></button>
)}
          </li>


          



          <li>
             <button onClick={() => handleProtectedNavigation('/carted-products')}><img src={cartIcon} alt="Cart" /></button>
          </li>

          <img
            id="userProfileImage"
            src="/path/to/profile-image.jpg"
            alt="Profile"
          />
          <span id="userName" style={{ textTransform: 'capitalize' }}>
            John
          </span>
        </ul>

        <div
          className={`menubar ${menuOpen ? 'an' : ''}`}
          onClick={toggleHamburger}
        >
          <div className={`singleBar1 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar2 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar3 ${menuOpen ? 'an' : ''}`}></div>
        </div>
      </header>
    </section>
  );
};

export default Header;
