import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../App.css';
import Swal from 'sweetalert2';
import logo from '../assets/Logo.svg';
import searchIcon from '../assets/search.svg';
import wishlistIcon from '../assets/wishlist.svg';
import cartIcon from '../assets/cart.svg';
import accountIcon from '../assets/account.svg';
import defaultAvatar from '../assets/np2.png';

const Header = () => {
  const [activeMenu, setActiveMenu] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInitial, setUserInitial] = useState('');
  const [fullName, setFullName] = useState('');
  const [showProfilePopup, setShowProfilePopup] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('access');
    const firstName = localStorage.getItem('first_name');
    const lastName = localStorage.getItem('last_name');
    setIsLoggedIn(!!token);
    if (firstName) {
      setUserInitial(firstName[0].toUpperCase());
      setFullName(`${firstName} ${lastName || ''}`.trim());
    }
  }, []);

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      if (searchQuery.trim() === '') {
        setSearchResults([]);
        return;
      }

      fetch(`/api/products/search/?q=${encodeURIComponent(searchQuery)}`)
        .then(res => res.json())
        .then(data => {
          setSearchResults(data.products || []);
        })
        .catch(err => console.error('Search error:', err));
    }, 300);

    return () => clearTimeout(delayDebounce);
  }, [searchQuery]);

  const handleLogin = () => navigate('/login');
  const handleLogout = () => {
    ['access', 'refresh', 'first_name', 'last_name'].forEach(key => localStorage.removeItem(key));
    setIsLoggedIn(false);
    setUserInitial('');
    setShowProfilePopup(false);
    navigate('/');
  };

  const handleProtectedNavigation = (path) => {
    const token = localStorage.getItem('access');
    if (!token) {
      Swal.fire({
        icon: 'warning',
        title: 'Login Required',
        text: `Please login to view ${path.includes('wishlist') ? 'your wishlist' : 'your cart'}.`,
        confirmButtonColor: '#3085d6',
        confirmButtonText: 'Login',
      }).then(result => {
        if (result.isConfirmed) navigate('/login');
      });
      return;
    }
    navigate(path);
  };

  const toggleHamburger = () => {
    const toggled = !menuOpen;
    setMenuOpen(toggled);
    document.body.style.overflow = toggled ? 'hidden' : 'auto';
  };

  const toggleSearch = () => setShowSearch(!showSearch);
  const toggleProfilePopup = () => setShowProfilePopup(!showProfilePopup);

  const handleSearchSelect = (productId) => {
    setSearchQuery('');
    setSearchResults([]);
    navigate(`/product/${productId}`);
  };

  return (
    <section className="wrapper">
      <header>
        <h1>
          <a href="/"><img src={logo} alt="Logo" /></a>
        </h1>

        <ul className={`middle ${menuOpen ? 'show' : ''}`}>
          {['Shop', 'Men', 'Women', 'Combos', 'Joggers'].map((label) => (
            <li key={label} className={activeMenu === label ? 'active' : ''}>
              <a href={`/${label.toLowerCase()}`} onClick={() => setActiveMenu(label)}>
                {label}
              </a>
            </li>
          ))}
        </ul>

        <ul className="right">
          <li className="input" style={{ position: 'relative' }}>
            <img
              src={searchIcon}
              alt="Search"
              className="searchIcon"
              onClick={toggleSearch}
              style={{ cursor: 'pointer' }}
            />
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search for products"
              className={showSearch ? 'showinput' : ''}
              style={{ position: 'relative' }}
            />

            {searchResults.length > 0 && (
              <ul className="search-dropdown">
                {searchResults.map((product) => (
                  <li key={product.id} onClick={() => handleSearchSelect(product.id)}>
                    <img src={product.image} alt={product.name} />
                    <div>
                      <p>{product.name}</p>
                      <span>₹{product.price}</span>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </li>

          <li>
            <button onClick={() => handleProtectedNavigation('/liked-products')}>
              <img src={wishlistIcon} alt="Wishlist" />
            </button>
          </li>

          <li style={{ position: 'relative' }}>
            {!isLoggedIn ? (
              <button onClick={handleLogin}><img src={accountIcon} alt="Login" /></button>
            ) : userInitial ? (
              <div onClick={toggleProfilePopup} className="user-circle">{userInitial}</div>
            ) : (
              <img src={defaultAvatar} alt="User" onClick={toggleProfilePopup} className="avatar" />
            )}

            {showProfilePopup && (
              <div className="profile-popup">
                <p>{fullName || 'Guest User'}</p>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </li>

          <li>
            <button onClick={() => handleProtectedNavigation('/carted-products')}>
              <img src={cartIcon} alt="Cart" />
            </button>
          </li>
        </ul>

        <div className={`menubar ${menuOpen ? 'an' : ''}`} onClick={toggleHamburger}>
          <div className={`singleBar1 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar2 ${menuOpen ? 'an' : ''}`}></div>
          <div className={`singleBar3 ${menuOpen ? 'an' : ''}`}></div>
        </div>
      </header>
    </section>
  );
};

export default Header;
