import { useEffect, useState } from 'react';
import Header from '../../screens/Header';
import Footer from '../../screens/Footer';

function CartedProducts() {
  const [cartedProducts, setCartedProducts] = useState([]);
  const token = localStorage.getItem('access');

  useEffect(() => {
    if (!token) return;

    fetch('http://localhost:8000/carted-products/', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setCartedProducts(data))
      .catch((err) => console.error('Error fetching carted products:', err));
  }, [token]);

  const handleRemoveFromCart = (productId) => {
    fetch(`http://localhost:8000/category/product/${productId}/cart/`, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then(() => {
        setCartedProducts((prev) => prev.filter((item) => item.id !== productId));
      })
      .catch((err) => console.error('Error removing from cart:', err));
  };

  if (!token) return null;

  return (
    <>
      <Header />

      <section id="similar" style={{ borderTop: '1px solid black' }}>
        <section className="wrapper">
          <section className="similar" style={{ paddingTop: '40px' }}>
            <div className="top">
              <div className="violet"></div>
              <h3>Your Cart</h3>
            </div>

            {cartedProducts.length === 0 ? (
              <p>Your cart is empty.</p>
            ) : (
              <ul className="bottom" style={{ display: 'flex', flexWrap: 'wrap' }}>
                {cartedProducts.map((product) => (
                  <li key={product.id} style={{ position: 'relative' }}>
                    <a href={`/category/product/${product.id}`} style={{ width: '100%', display: 'block' }}>
                      <div>
                        <img
                          src={`http://localhost:8000${product.image}`}
                          alt={product.name}
                          style={{ width: '100%', height: '350px' }}
                        />
                        <h4
                          style={{
                            textAlign: 'center',
                            marginTop: '10px',
                            color: 'black',
                            fontWeight: 'normal',
                          }}
                        >
                          {product.name}
                        </h4>
                      </div>
                    </a>
                    <button
                      onClick={() => handleRemoveFromCart(product.id)}
                      style={{
                        position: 'absolute',
                        top: 0,
                        right: 0,
                        backgroundColor: 'white',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '18px',
                      }}
                    >
                      ❌
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </section>
        </section>
      </section>

      <Footer />
    </>
  );
}

export default CartedProducts;
